import Todo from './todo'

export default function TodoList ({ newTodo, createTodo, setNewTodo, todos, completedTodos, moveToCompleted, deleteTodo }){
    return(
        <>
            Add New Todo:<input type="text" 
        value={newTodo.title} 
        onChange={(e) => {
            setNewTodo({...newTodo, title: e.target.value})
        }} 
        onKeyDown={(e) => {
            e.key === 'Enter' && createTodo()
        }}
        />
        <h3>Todos</h3>
        {todos.map(todo => {
            return(
                <div key={todo._id}>{todo.title} 
                    <button onClick={() => moveToCompleted(todo._id) }>Complete</button>
                </div>
            )})
        }
        <h3>Completed Todos</h3>
        {completedTodos.map(todo => {
            return(
                <div key={todo._id}>{todo.title} 
                    <button onClick={() => deleteTodo(todo._id) }>Delete</button>
                </div>
            )})
        }
        </>
    )
}
